<?php$field_val_num = '';if(!empty($field['value']) && is_array($field['value']) && isset($field['value'])){   $counter = end(explode("_", $key));   $field_val_num = !empty($field['value'][$counter]) ? $field['value'][$counter] : '';} else {   $field_val_num = !empty($field['value']) ? $field['value'] : '';}?><div class="controls" style="position: relative">   <input type="text" class="input-text" name="<?php echo esc_attr( isset( $field['name'] ) ? $field['name'] : $key ); ?>" id="<?php echo isset( $field['id'] ) ? esc_attr( $field['id'] ) :  esc_attr( $key ); ?>" attribute="<?php echo esc_attr( isset( $field['attribute'] ) ? $field['attribute'] : '' ); ?>" placeholder="<?php echo esc_attr( $field['placeholder'] ); ?>" value="<?php echo isset( $field_val_num ) ? esc_attr( $field_val_num ) : ''; ?>" maxlength="<?php echo ! empty( $field['maxlength'] ) ? $field['maxlength'] : ''; ?>" <?php if ( ! empty( $field['required'] ) ) echo 'required'; ?> data-picker="datepicker" />   <?php if ( ! empty( $field['description'] ) ) : ?><small class="description"><?php echo $field['description']; ?></small><?php endif; ?></div>