<select name="<?php echo esc_attr( isset( $field['name'] ) ? $field['name'] : $key ); ?>" id="<?php echo esc_attr( $key ); ?>" <?php if ( ! empty( $field['required'] ) ) echo 'required'; ?> attribute="<?php echo esc_attr( isset( $field['attribute'] ) ? $field['attribute'] : '' ); ?>" >	<?php foreach ( $field['options'] as $key => $value ) : ?>		<option value="<?php echo esc_attr( $key ); ?>" <?php if( $field['value'] == $key ){ echo 'selected="selected"'; } ?>><?php echo esc_html( $value ); ?></option>	<?php endforeach; ?></select><?php if ( ! empty( $field['description'] ) ) : ?><small class="description"><?php echo $field['description']; ?></small><?php endif; ?>