<?php$field_val_num = '';if(!empty($field['value']) && is_array($field['value'])){    $field_val_num = $field['value'][end(explode("_", $key))];} else {    $field_val_num = !empty($field['value']) ? $field['value'] : '';}?><select name="<?php echo esc_attr( isset( $field['name'] ) ? $field['name'] : $key ); ?>" id="<?php echo esc_attr( $key ); ?>" <?php if ( ! empty( $field['required'] ) ) echo 'required'; ?> attribute="<?php echo esc_attr( isset( $field['attribute'] ) ? $field['attribute'] : '' ); ?>" >	<?php foreach ( $field['options'] as $key => $value ) : 		$field_val = (!empty($field_val_num) && $field_val_num === $key) ? 'selected' : '';		?>		<option value="<?php echo esc_attr( $key ); ?>" <?php echo $field_val; ?>><?php echo esc_html( $value ); ?></option>	<?php endforeach; ?></select><?php if ( ! empty( $field['description'] ) ) : ?><small class="description"><?php echo $field['description']; ?></small><?php endif; ?>